import "./index.css";
//Import React & useState
import React, { useState, useEffect } from "react";

function App() {
  //State Of Search Bar, set default to empty string
  const [newPokemon, setNewPokemon] = useState("");
  //State Of Returned Pokemon, set default to empty object
  const [pokemon, setPokemon] = useState({
    id: undefined,
    name: "",
    type: "",
  });

  // Similar to componentDidMount and componentDidUpdate:
  function GetData() {
    useEffect(() => {
      fetch(`https://pokeapi.co/api/v2/pokemon/${newPokemon}`)
        .then((response) => {
          response.json().then((pokemonData) => {
            const pokemonID = pokemonData.id;
            const pokemonName = pokemonData.name;
            const typeObject = pokemonData.types[0];
            const pokemonType = typeObject.type.name;
            console.log(pokemonType);
            setPokemon({
              id: pokemonID,
              name: pokemonName,
              type: pokemonType,
            });
          });
        })
        .catch((e) => {
          console.log(`Error is: ${e}`);
        });
    }, []);
  }

  return (
    <div className="App">
      <h1>Pokédex</h1>
      <p>(Developed /w ReactJS, APIs & Boostrap)</p>
      <div id="mainContainer">
        <div className="input-group">
          <input
            type="search"
            className="form-control rounded"
            placeholder="Search For A Pokémon"
            value={newPokemon}
            onChange={(e) => {
              setNewPokemon(e.target.value);
            }}
          />
          <button type="button" className="btn btn-primary">
            search
          </button>
        </div>
        <div id="topContainer">
          <div id="imgContainer">
            <img
              className="images"
              src={
                "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png"
              }
            />
          </div>
          <div className="genericCard" id="nameContainer">
            <div className="label">Name</div>
            <div className="value" key={pokemon.id}>
              {pokemon.name}
            </div>
          </div>
          <div className="genericCard" id="typeConatiner">
            <div className="label">Type</div>
            <div className="value" key={pokemon.id}>
              {pokemon.type}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
