import "./index.css";
//Import React & useState
import React, { useState, useEffect } from "react";

function App() {
  //State Of Search Bar, set default to empty string
  const [newPokemon, setNewPokemon] = useState("");

  // Similar to componentDidMount and componentDidUpdate:
  useEffect(() => {
    fetch(`https://pokeapi.co/api/v2/pokemon/${newPokemon}`)
      .then((response) => {
        console.log(response.json());
      })
      .then((data) => {
        const pokemonData = data;
      })
      .catch((e) => {
        console.log("Error" + e);
      });
  });

  return (
    <div className="App">
      <h1>Pokédex</h1>
      <p>(Developed /w ReactJS, APIs & Boostrap)</p>
      <div id="mainContainer">
        <div class="input-group">
          <input
            type="search"
            class="form-control rounded"
            placeholder="Search For A Pokémon"
            value={newPokemon}
            onChange={(e) => {
              setNewPokemon(e.target.value);
            }}
          />
          <button type="button" class="btn btn-primary">
            search
          </button>
        </div>
        <div id="topContainer">
          <div id="imgContainer">
            <img
              className="images"
              src={
                "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png"
              }
            />
          </div>
          <div className="genericCard" id="nameContainer">
            <div className="label">Name</div>
            <div className="value"></div>
          </div>
          <div className="genericCard" id="typeConatiner">
            <div className="label">Type</div>
            <div className="value"></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
